tinymce.PluginManager.add("youtube",function(b,a){b.addButton("youtube",{text:"Youtube",icon:false,onclick:function(){b.windowManager.open({title:"youtube plugin",body:[{type:"textbox",name:"title",label:"Title"}],onsubmit:function(d){var c='<iframe id="ytplayer" type="text/html" width="640" height="390" src="http://www.youtube.com/embed/'+d.data.title+'?autoplay=0" frameborder="0"/>';parent.tinyMCE.activeEditor.windowManager.close(window);b.insertContent(c)}})}});b.addMenuItem("youtube",{text:"youtube plugin",context:"tools",onclick:function(){b.windowManager.open({title:"TinyMCE site",url:"http://www.tinymce.com",width:800,height:600,buttons:[{text:"Close",onclick:"close"}]})}})});